            $priceRange = $serviceData[3];
            $price = rand($priceRange[0], $priceRange[1]);

            $service = new Service();
            $service->name = $serviceData[0] . ' - ' . $provider;
            $service->description = $serviceData[1] . ' –≤—ñ–¥ –ø—Ä–æ–≤–∞–π–¥–µ—Ä–∞ "' . $provider . '"';
            $service->duration = $serviceData[2];
            $service->price = $price;
            $service->is_active = 1;

            if ($service->save()) {
                $createdCount++;
                if ($createdCount % 20 == 0) {
                    $this->stdout("  ‚úì Created $createdCount services...\n");
                }
            }
        }

        $this->stdout("\n‚úÖ Successfully created $createdCount services!\n");
